import React from "react";
import styled from "styled-components";
import itunes from "./itunes.svg";
import instagram from "./instagram.png";
import spotify from "./spotify.png";
import youtube from "./youtube.png";
import applemusic from "./applemusic.svg";
import tidal from "./tidal.png";
import soundcloud from "./soundcloud.png";
import amazon from "./amazon.png";
import google from "./google.png";
import laurent from "./laurent.png";
import { Links } from "./Link";
import "./App.css";

function App() {
  const links = [
    [spotify, "https://bit.ly/LAURENT-SPOTIFY", "play"],
    [applemusic, "https://bit.ly/LAURENT-APPLE-MUSIC", "play"],
    [tidal, "https://bit.ly/LAURENT-TIDAL", "play"],
    [youtube, "https://bit.ly/LAURENT-YOUTUBE-CHANNEL", "play"],
    [soundcloud, "https://bit.ly/LAURENT-SOUNDCLOUD", "play"],
    [itunes, "https://bit.ly/LAURENT-SOUNDCLOUD", "buy"],
    [google, "https://bit.ly/LAURENT-SOUNDCLOUD", "buy"],
    [amazon, "https://bit.ly/LAURENT-SOUNDCLOUD", "buy"]
  ];
  return (
    <div className="App">
      <App.Container>
        <App.Profile>
          <App.ProfilePic src={laurent} />
          <App.Name>LAURENT</App.Name>
        </App.Profile>
        <Links links={links} />
      </App.Container>
    </div>
  );
}

App.Container = styled.div`
  width: 30%;
  max-width: 640px;
  @media only screen and (max-width: 766px) {
    width: 100%;
  }
`;

App.Profile = styled.div`
  margin-top: 20px;
  margin-bottom: 20px;
  width: 100%;
`;

App.ProfilePic = styled.img`
  width: 50%;
  height: auto;
  max-width: 210px;
`;

App.Name = styled.p`
  font-weight: 600;
  margin-left: 20px;
  margin-top: 10px;
`;

export default App;
